import Immutable from 'immutable';
import Stream from './stream';

/**
 * A Funnel is a special type of Stream that combines the states from feeder
 * streams.
 * @extends Stream
 */
class Funnel extends Stream {

  /**
   * A Funnel is a special type of Stream that combines the states from feeder
   * streams. Once all of the feeder streams have sent state, the Funnel will send the combined
   * state to any observers. When creating the Funnel, the sourceMap describes the shape of
   * the combined state. The key for each stream will be the key used to access the state of
   * that stream on the combined state object.
   *
   * @param  {Immutable.Map} sourceMap - a Map of the feeder streams.
   * @return {Funnel}           returns an instance of the Funnel stream.
   */
  constructor (sourceMap) {
    super(Immutable.Map());

    this.primed = false;
    this.sourceKeys = sourceMap.keySeq();
    this.subscribers = sourceMap.map((sourceStream, key) => {
      return sourceStream.subscribe(newState => {
        this.state = this.state.set(key, newState);
      });
    });
  }

  /**
   * sendState - overwrites Stream's `sendState` by enforcing that all feeder streams have
   * sent state before sending state on to observers. Once a Funnel is `primed` it will pass
   * state on, regardless if one stream sends `undefined`.
   *
   * @access private
   * @param  {Immutable} newState - new state (received internally from Stream's `next` method)
   */
  sendState (newState) {
    if(this.primed){
      super.sendState(newState);
    } else {
      if (this.sourceKeys.every(source => newState.get(source))) {
        this.primed = true;
        super.sendState(newState);
      } else {
        this._state = newState;
      }
    }
  }

  /**
   * destroy - cleans up a Funnel's feeder subscribers.
   *
   * @access public
   */
  destroy () {
    this.subscribers.forEach(subscriber => subscriber.unsubscribe());
  }

}

export default Funnel;

import Stream from './stream';
import Stack from './stack';
import Funnel from './funnel';
import View from './view';

export { Stream, Stack, Funnel, View };

/** Requires Immutable */
import Immutable from 'immutable';

/**
 * A Stack isn't a stream, but can be used in conjunction with streams, actions, and
 * transformers to make debugging much easier.
 */
class Stack {
  constructor (size = 20, debug = false) {
    this.stack = Immutable.List().setSize(size);
    this.debug = debug;
  }

  addToStack (item) {
    if (this.debug) {
      this.stack = this.stack.shift().push(item);
    }
  }

  pushAction (fn, args) {
    this.addToStack(Immutable.Map({type: 'ACTION', name: fn, args: args}));
  }

  pushState (name, state, streamType) {
    this.addToStack(Immutable.Map({ type: 'STATE', name , state, streamType }));
    if (this.testFn && this.testFn(state)) {
      this.dumpWhenCb(this.dump());
    }
  }

  pushTransformer (fn, args) {
    this.addToStack(Immutable.Map({ type: 'TRANSFORMER', name: fn, args: args}));
  }

  pushLog (text) {
    this.addToStack(Immutable.Map({ type: 'LOG', text }));
  }

  dump () {
    return this.stack.filter(item => item);
  }

  dumpWhen (testFn, dumpFn) {
    this.testFn = testFn;
    this.dumpWhenCb = dumpFn;
  }

  dumpToLog (stateAccessor) {
    const toLog = {
      ACTION: item => console.log('%cACTION ' + '%c'+item.get('name') + '\n',
                                  'font-weight: bold; color: #629456', 'font-style: italic; color: #004',
                                  item.get('args')),
      LOG: item => console.log('%cLOG\n' + item.get('text'), 'background: #eee; color: #555'),
      TRANSFORMER: item => console.log('%cTRANSFORMER ' + '%c' + item.get('name') + '\n',
                                       'font-weight: bold; color: #D08A10', 'font-style: italic; color: #004',
                                       item.get('args')),
      STATE: (item, stateAccessor = []) => console.log('%cSTATE ' + (item.streamType ? '(' + item.streamType + ')' : '') + '%c' + item.get('name') + '\n',
                                            'font-weight: bold; color: #3B74C4', 'font-style: italic; color: #004',
                                            item.getIn(['state'].concat(stateAccessor)).toJS())
    };

    if (this.debug) {
      this.stack.map(item => {
        if(item){
          toLog[item.get('type')](item, stateAccessor);
        }
      });
    }
  }

  set size(newSize) {
    this.stack.setSize(newSize);
  }

  get size() {
    return this.stack.size;
  }
}

export default Stack;

/** Requires Immutable */
import Immutable from 'immutable';

/**
 * A Stream holds the canonical state of a part of an application, and pushes changes to that state
 * to any observers that are subscribed to the Stream.
 *
 */
class Stream {

  /**
   * constructor - When creating a new stream, an initial state is not required, but it is
   * encouraged. If an initial state is passed in, it should be an Immutable data structure.
   *
   * @param  {Immutable} initialState this is the state that the stream will start with.
   * @return {Stream} Instance of a Stream.
   */
  constructor (initialState) {
    if(initialState && !Immutable.Iterable.isIterable(initialState)) {
      throw new TypeError('initialState must be immutable');
    }

    this._state = initialState;
    this.observers = Immutable.List();
  }


  logStack (name, stack) {
    this.name = name;
    this.stack = stack;
  }
  /**
   * subscribe - takes a callback function that is executed any time the stream received _new_
   * state. The callback should accept `state` as an argument. If the stream currently has state,
   * it will be passed to the callback immediately upon subscription.
   *
   * An observer gets added to an `observers` array. State changes will propagate to any observers
   * in this array.
   *
   * It will return an object (we'll call a _subscriber_) that has an `unsubscribe` method. This
   * method will remove the observer from the `observers` List and should be called anytime the
   * observer is done listening to the stream. Failing to do so could cause memory leaks.
   *
   * @access public
   * @param  {function} observer - the callback function to be executed whenever state updates
   * @return {object}            - a _subscriber_ object with an `unsubscribe` method
   */
  subscribe (observer) {
    this.observers = this.observers.push(observer);
    if(this._state) { observer(this._state); }
    return {
      unsubscribe: () => {
        const idx = this.observers.indexOf(observer);
        if (idx >= 0) {
          this.observers = this.observers.delete(this.observers.indexOf(observer));
        } else {
          throw new RangeError('Observer is not subscribed to this stream.');
        }
      }
    };
  }

  /**
   * sendState - Gets called whenever the Stream receives _new_ state. `sendState` first updates
   * the Streams internal `_state` and then each observer in the `observers` List will be called
   * with the new state as its argument.
   *
   * @access private
   * @param  {Immutable} newState the Stream's new state.
   */
  sendState (newState) {
      this._state = newState;
      if (this.stack) { this.stack.pushState(this.name, this._state, this.type); }
      this.observers.forEach(observer => observer(this._state));
  }

  /**
   * next - Receives new state (externally, ideally through a transformer) and verifies that the
   * new state is different from the Stream's current state before passing on to `sendState` (and,
   * all the observers, by extension).
   *
   * @access public
   * @param  {Immutable} newState
   */
  next (newState) {
    if(!Immutable.is(this._state, newState)){
      this.sendState(newState);
    }
  }

  /**
   * get state - a getter for returning the current internal state of the Stream.
   *
   * @access public
   * @return {Immutable} - current state contained by the Stream
   */
  get state(){
    return this._state;
  }

  /**
   * set state - a setter that's probably more clever than it needs to be. Instead of setting
   * the internal state, it calls `next`.
   *
   * ```javascript
   * stream.next(newState)
   * // is functionally the same as
   * stream.state = newState
   * ```
   *
   * Like I said, maybe too clever, but I thinks allow for a more delarative style when
   * writing transformers.
   *
   * @access public
   * @param  {type} newState description
   * @return {type}          description
   */
  set state(newState){
    this.next(newState);
  }
}

export default Stream;

import Stream from './stream';

/**
 * A View is a kind of Stream the that caches particular a shape of state.
 *
 * @extends Stream
 */
class View extends Stream {
  constructor (source, viewFn) {
    super();

    this.source = source;
    this.sourceSubscriber = source.subscribe( state => this.next(viewFn(state)) );
  }

  destroy () {
    this.sourceSubscriber.unsubscribe();
  }
}

export default View;
